/*\
title: $:/plugins/BenSweaterVest/cloudflare-saver/saver.js
type: application/javascript
module-type: saver

Cloudflare Functions saver for TiddlyWiki - Additional Saver Option

This saver allows TiddlyWiki to save to GitHub via a Cloudflare Function.
It works as an additional save option alongside other savers.

Features:
- Password authentication
- Auto-retry with exponential backoff
- Session password memory
- Visual notifications
- Comprehensive error handling

\*/
(function(){

  'use strict';

  /**
 * CloudflareSaver constructor
 * @param {Object} wiki - TiddlyWiki wiki object
 */
  const CloudflareSaver = function(wiki) {
    this.wiki = wiki;
    this.sessionPassword = null; // Store password for session if enabled
  };

  CloudflareSaver.prototype.save = function(text, method, callback, options) {
    const self = this;
    options = options || {};

    // Check if this saver is enabled
    const enabled = self.wiki.getTiddlerText('$:/config/cloudflare-saver/enabled', 'no') === 'yes';
    if(!enabled) {
      return false; // Let other savers handle it
    }

    // Validate configuration before claiming to handle the save
    const endpoint = self.wiki.getTiddlerText('$:/config/cloudflare-saver/endpoint', '');
    if(!endpoint || endpoint.trim() === '') {
      return false; // Let other savers handle it if not configured
    }

    // We can handle save and autosave methods
    if(method !== 'save' && method !== 'autosave') {
      return false;
    }

    const config = {
      endpoint,
      timeout: Math.max(5, parseInt(self.wiki.getTiddlerText('$:/config/cloudflare-saver/timeout', '30')) || 30) * 1000,
      notifications: self.wiki.getTiddlerText('$:/config/cloudflare-saver/notifications', 'yes') === 'yes',
      autoRetry: self.wiki.getTiddlerText('$:/config/cloudflare-saver/auto-retry', 'yes') === 'yes',
      rememberPassword: self.wiki.getTiddlerText('$:/config/cloudflare-saver/remember-password', 'no') === 'yes',
      debug: self.wiki.getTiddlerText('$:/config/cloudflare-saver/debug', 'no') === 'yes'
    };

    if(config.debug) {
      console.log('[CloudflareSaver] Starting save process');
    }

    let password = null;
    if(config.rememberPassword && self.sessionPassword) {
      password = self.sessionPassword;
    } else {
      password = prompt('Enter Cloudflare save password:');
      if (!password) {
        callback('Cloudflare save cancelled by user');
        return false;
      }
      if(config.rememberPassword) {
        self.sessionPassword = password;
      }
    }

    if(config.notifications && typeof $tw !== 'undefined' && $tw.notifier) {
      $tw.notifier.display('$:/plugins/BenSweaterVest/cloudflare-saver/notifications/saving');
    }

    self._performSave(text, password, callback, config, 0);
    return true;
  };

  CloudflareSaver.prototype._performSave = async function(text, password, callback, config, retryCount) {
    const self = this;
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), config.timeout);

    try {
      const payload = {
        content: text,
        password,
        timestamp: new Date().toISOString(),
        retryCount
      };

      const response = await fetch(config.endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(payload),
        signal: controller.signal
      });

      clearTimeout(timeoutId);

      if(response.ok) {
        if(config.debug) {
          console.log('[CloudflareSaver] Save successful');
        }
        callback(null);
        if(config.notifications && typeof $tw !== 'undefined' && $tw.notifier) {
          $tw.notifier.display('$:/plugins/BenSweaterVest/cloudflare-saver/notifications/success');
        }
      } else {
        const responseText = await response.text();
        self._handleSaveError(response.status, response.statusText, responseText, password, text, callback, config, retryCount);
      }
    } catch(error) {
      clearTimeout(timeoutId);

      // Handle abort/timeout separately
      if (error.name === 'AbortError') {
        self._handleSaveError(0, 'Request timeout', '', password, text, callback, config, retryCount);
      } else {
        self._handleSaveError(0, 'Network error', error.message, password, text, callback, config, retryCount);
      }
    }
  };

  CloudflareSaver.prototype._handleSaveError = function(status, statusText, responseText, password, text, callback, config, retryCount) {
    const self = this;
    const maxRetries = config.autoRetry ? 3 : 0;

    let errorMsg = 'Cloudflare save failed';
    if(status) {
      errorMsg += `: HTTP ${  status}`;
      if(statusText) {
        errorMsg += ` ${  statusText}`;
      }
    }

    // Parse response for detailed error information
    if(responseText) {
      try {
        const response = JSON.parse(responseText);
        if(response.error) {
          errorMsg += ` - ${  response.error}`;
        }
        // Add rate limit information if available
        if(response.resetIn) {
          errorMsg += ` (retry in ${  response.resetIn  } seconds)`;
        }
      } catch(e) {
        if(responseText.length < 200) {
          errorMsg += ` - ${  responseText}`;
        }
      }
    }

    // Handle specific HTTP status codes
    if(status === 401) {
      self.sessionPassword = null;
      errorMsg = 'Cloudflare authentication failed. Please check your password.';
    } else if(status === 429) {
      errorMsg = 'Rate limit exceeded. Please wait a minute before trying again.';
    } else if(status === 413) {
      errorMsg = 'Content too large. Your TiddlyWiki exceeds the maximum allowed size.';
    } else if(status === 409) {
      errorMsg = 'Conflict detected. Another save may be in progress.';
    }

    if(config.debug) {
      console.error('[CloudflareSaver] Error:', errorMsg, {
        status,
        retryCount,
        willRetry: retryCount < maxRetries && status !== 401
      });
    }

    // Retry logic (don't retry auth failures or rate limits)
    if(retryCount < maxRetries && status !== 401 && status !== 429) {
      const retryDelay = Math.min(1000 * Math.pow(2, retryCount), 10000);
      if(config.debug) {
        console.log(`[CloudflareSaver] Retrying in ${  retryDelay / 1000  } seconds...`);
      }
      setTimeout(() => {
        self._performSave(text, password, callback, config, retryCount + 1);
      }, retryDelay);
    } else {
      callback(errorMsg);
      if(config.notifications && typeof $tw !== 'undefined' && $tw.notifier) {
        $tw.notifier.display('$:/plugins/BenSweaterVest/cloudflare-saver/notifications/failure');
      }
    }
  };

  CloudflareSaver.prototype.info = {
    name: 'cloudflare',
    priority: 2000, // High priority - when enabled, prefer this over download saver
    capabilities: ['save', 'autosave']
  };

  // Export saver info at module level
  exports.info = {
    name: 'cloudflare',
    priority: 2000,
    capabilities: ['save', 'autosave']
  };

  // Export module-level functions as required by TiddlyWiki
  exports.canSave = function(wiki) {
    // Use $tw.wiki global instead of wiki parameter during initialization
    const enabled = $tw.wiki.getTiddlerText('$:/config/cloudflare-saver/enabled', 'no') === 'yes';
    const endpoint = $tw.wiki.getTiddlerText('$:/config/cloudflare-saver/endpoint', '');
    // Only claim we can save if both enabled AND endpoint is configured
    return enabled && endpoint && endpoint.trim() !== '';
  };

  exports.create = function(wiki) {
    return new CloudflareSaver(wiki);
  };

})();

/*\
title: $:/plugins/BenSweaterVest/cloudflare-saver/startup.js
type: application/javascript
module-type: startup

Startup module for Cloudflare Saver plugin - Register as additional saver
\*/
(function(){

  'use strict';

  exports.name = 'cloudflare-saver-startup';
  exports.platforms = ['browser'];
  exports.after = ['startup'];
  exports.synchronous = true;

  exports.startup = function() {
    // Don't automatically set as default saver - let users choose

    // Set up configuration defaults
    const configDefaults = {
      '$:/config/cloudflare-saver/endpoint': '',
      '$:/config/cloudflare-saver/timeout': '30',
      '$:/config/cloudflare-saver/notifications': 'yes',
      '$:/config/cloudflare-saver/auto-retry': 'yes',
      '$:/config/cloudflare-saver/remember-password': 'no',
      '$:/config/cloudflare-saver/debug': 'no',
      '$:/config/cloudflare-saver/enabled': 'no'
    };

    Object.keys(configDefaults).forEach((title) => {
      if(!$tw.wiki.getTiddler(title)) {
        $tw.wiki.addTiddler(new $tw.Tiddler({
          title,
          text: configDefaults[title]
        }));
      }
    });

    const enabled = $tw.wiki.getTiddlerText('$:/config/cloudflare-saver/enabled', 'no') === 'yes';
    const debug = $tw.wiki.getTiddlerText('$:/config/cloudflare-saver/debug', 'no') === 'yes';

    if (debug) {
      console.log(`[CloudflareSaver] Plugin loaded successfully${  enabled ? ' and enabled' : ' (disabled)'}`);
    }
  };

})();

/*\
title: $:/plugins/BenSweaterVest/cloudflare-saver/test-action.js
type: application/javascript
module-type: widget

Action widget to test Cloudflare save connection

\*/
(function(){

  'use strict';

  const Widget = require('$:/core/modules/widgets/widget.js').widget;

  const TestCloudflareAction = function(parseTreeNode,options) {
    this.initialise(parseTreeNode,options);
  };

  TestCloudflareAction.prototype = new Widget();

  TestCloudflareAction.prototype.render = function(_parent,_nextSibling) {
    this.computeAttributes();
    this.execute();
  };

  TestCloudflareAction.prototype.execute = function() {
    // Nothing to execute
  };

  TestCloudflareAction.prototype.refresh = function(_changedTiddlers) {
    return false;
  };

  TestCloudflareAction.prototype.invokeAction = function(_triggeringWidget,_event) {
    const self = this;

    // Get configuration
    const endpoint = $tw.wiki.getTiddlerText('$:/config/cloudflare-saver/endpoint', '');
    const enabled = $tw.wiki.getTiddlerText('$:/config/cloudflare-saver/enabled', 'no') === 'yes';

    // Validate configuration
    if (!enabled) {
      self.showAlert('Cloudflare Saver is not enabled. Please enable it in the settings above.', 'error');
      return true;
    }

    if (!endpoint || endpoint.trim() === '') {
      self.showAlert('No Cloudflare Function endpoint configured. Please enter your endpoint URL above.', 'error');
      return true;
    }

    // Get password
    const password = prompt('Enter your Cloudflare save password to test the connection:');
    if (!password) {
      self.showAlert('Test cancelled - no password provided.', 'info');
      return true;
    }

    // Show loading state
    self.showNotification('$:/plugins/BenSweaterVest/cloudflare-saver/notifications/saving');

    // Create test content
    const testContent = $tw.wiki.renderTiddler('text/plain', '$:/core/save/all');

    // Attempt to save
    const timeout = Math.max(5, parseInt($tw.wiki.getTiddlerText('$:/config/cloudflare-saver/timeout', '30')) || 30) * 1000;
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), timeout);

    const payload = {
      content: testContent,
      password,
      timestamp: new Date().toISOString(),
      retryCount: 0
    };

    fetch(endpoint, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(payload),
      signal: controller.signal
    })
      .then((response) => {
        clearTimeout(timeoutId);

        if (response.ok) {
          return response.json().then((data) => {
            self.showNotification('$:/plugins/BenSweaterVest/cloudflare-saver/notifications/success');
            self.showAlert(
              `✅ Test successful!\n\nConnection to Cloudflare Function verified.\nCommit SHA: ${data.commit || 'N/A'}\n\nYour Cloudflare Saver is configured correctly.`,
              'success'
            );
          });
        } else {
          return response.text().then((errorText) => {
            let errorMsg = `HTTP ${response.status} ${response.statusText}`;
            try {
              const errorData = JSON.parse(errorText);
              if (errorData.error) {
                errorMsg = errorData.error;
              }
            } catch(e) {
              // Use status text
            }

            self.showNotification('$:/plugins/BenSweaterVest/cloudflare-saver/notifications/failure');
            self.showAlert(
              `❌ Test failed!\n\nError: ${errorMsg}\n\nPlease check:\n• Your endpoint URL is correct\n• Your password matches SAVE_PASSWORD in Cloudflare\n• Environment variables are configured\n• The Cloudflare Function is deployed`,
              'error'
            );
          });
        }
      })
      .catch((error) => {
        clearTimeout(timeoutId);

        let errorMsg = 'Network error';
        if (error.name === 'AbortError') {
          errorMsg = 'Request timeout';
        } else {
          errorMsg = error.message;
        }

        self.showNotification('$:/plugins/BenSweaterVest/cloudflare-saver/notifications/failure');
        self.showAlert(
          `❌ Test failed!\n\nError: ${errorMsg}\n\nPlease check:\n• Your internet connection\n• The endpoint URL is accessible\n• CORS is configured to allow your origin\n• The Cloudflare Function is deployed`,
          'error'
        );
      });

    return true; // Prevent default action
  };

  TestCloudflareAction.prototype.showAlert = function(message, _type) {
    // Use TiddlyWiki's modal dialog
    const tempTiddler = '$:/temp/cloudflare-test-result';
    $tw.wiki.addTiddler(new $tw.Tiddler({
      title: tempTiddler,
      text: message,
      type: 'text/plain'
    }));

    $tw.modal.display(tempTiddler, {
      downloadLink: null
    });

    // Clean up temp tiddler after a delay
    setTimeout(() => {
      $tw.wiki.deleteTiddler(tempTiddler);
    }, 500);
  };

  TestCloudflareAction.prototype.showNotification = function(tiddlerTitle) {
    if (typeof $tw !== 'undefined' && $tw.notifier) {
      $tw.notifier.display(tiddlerTitle);
    }
  };

  exports['action-test-cloudflare'] = TestCloudflareAction;

})();
